import Link from "next/link";
import fetch from "isomorphic-unfetch";

const Index = props => (
  <div>
    <h1>Products List</h1>
    <ul>
      {props.products}
      {props.products.map(show => (
        <li key={products.id}>
          <Link href="/p/[id]" as={`/p/${products.id}`}>
            <a>{products.name}</a>
          </Link>
        </li>
      ))}
    </ul>
  </div>
);

Index.getProducts = async function() {
  const res = await fetch("http://localhost:8080/products?_page=1&_limit=20");
  const data = await res.json();

  console.log(`Product data fetched. Count: ${data.length}`);

  return {
    products: data
  };
};

export default Index;
