import Link from "next/link";
import fetch from "isomorphic-unfetch";

const LIMIT_PER_PAGE = 20;
let ALL_PRODUCTS_LENGTH;

const Index = props => (
  <div className="IndexPage">
    <h1>Products List</h1>
    <div className="ProductsContainer">
      {props.products.map(product => (
        <Link href="/product/[id]" as={`/product/${product.id}`}>
          <div className="ProductThumbnail">
            <span className="ProductName">{product.title}</span>
            <span className="ProductPrice">{product.price}€</span>
            <img
              className="ProductImage"
              src={product.product_image}
              alt={`${product.title} image`}
            />
          </div>
        </Link>
      ))}
    </div>
    <div className="Pagination">
      <span className="PreviousPage">{props.previousPage}</span>
      <span className="CurrentPage">{props.currentPage}</span>
      <span className="NextPage">{props.nextPage}</span>
    </div>
    <style jsx>{`
      h1 {
        font-family: "Arial", sans-serif;
        display: inline-block;
        width: 100%;
        text-align: center;
      }

      .IndexPage {
        position: relative;
        width: 100%;
        height: auto;
      }

      .ProductsContainer {
        width: 1200px;
        height: 1300px;
        margin: auto;
      }

      .ProductThumbnail {
        position: relative;
        width: 200px;
        height: 300px;
        float: left;
        overflow: hidden;
        background-color: rgba(0, 0, 0, 0.5);
        margin: 10px 20px;
      }

      .ProductName {
        font-size: 20px;
        font-family: sans-serif;
        display: inline-block;
        width: 100%;
        text-align: center;
        top: 20px;
        position: relative;
        background-color: rgba(255, 255, 255, 0.75);
      }

      .ProductPrice {
        position: absolute;
        display: inline-block;
        top: 255px;
        width: 100%;
        text-align: center;
        font-size: 25px;
        right: 0px;
        background-color: rgba(255, 255, 255, 0.75);
      }

      .ProductImage {
        height: 100%;
        position: absolute;
        top: 0px;
        right: -30px;
        z-index: -10;
      }

      ul {
        padding: 0;
      }

      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </div>
);

Index.getInitialProps = async function({ query }) {
  if (!ALL_PRODUCTS_LENGTH) {
    const allProducts = await fetch("http://localhost:8080/products");
    const dataAllProducts = await allProducts.json();
    ALL_PRODUCTS_LENGTH = dataAllProducts.length;
  }

  const products = await fetch(
    `http://localhost:8080/products?_page=${query.p}&_limit=${LIMIT_PER_PAGE}`
  );
  const dataProduct = await products.json();

  const previousPage = query.p - 1 <= 0 ? 0 : query.p - 1;
  const nextPage =
    (query.p + 1) * LIMIT_PER_PAGE >= ALL_PRODUCTS_LENGTH
      ? query.p
      : query.p + 1;

  console.log("Pagination is", query.p + 1, LIMIT_PER_PAGE);
  console.log("Pagination is", ALL_PRODUCTS_LENGTH);

  return {
    totalProducts: ALL_PRODUCTS_LENGTH,
    products: dataProduct,
    currentPage: query.p,
    previousPage: previousPage,
    nextPage: nextPage
  };
};

export default Index;
