import Link from "next/link";
import fetch from "isomorphic-unfetch";

const Index = props => (
  <div>
    <h1>Products List</h1>
    <div>
      {props.products.map(product => (
        <div>
          <Link href="/p/[id]" as={`/p/${product.id}`}>
            <span>{product.title}</span>
          </Link>
          <span> - {product.price}â‚¬</span>
          <img src={product.product_image} alt={`{product.name} image`} />
        </div>
      ))}
    </div>
  </div>
);

Index.getInitialProps = async function() {
  const res = await fetch("http://localhost:8080/products?_page=1&_limit=20");
  const data = await res.json();

  console.log(`Product data fetched. Count: ${data.length}`);

  return {
    products: data
  };
};

export default Index;
