import Link from "next/link";
import fetch from "isomorphic-unfetch";

const Index = props => (
  <div>
    <h1>Products List</h1>
    <div className="ProductsContainer">
      {props.products.map(product => (
        <Link href="/product/[id]" as={`/product/${product.id}`}>
          <div className="ProductThumbnail">
            <span className="ProductName">{product.title}</span>
            <span className="ProductPrice">{product.price}â‚¬</span>
            <img
              className="ProductImage"
              src={product.product_image}
              alt={`{product.name} image`}
            />
          </div>
        </Link>
      ))}
    </div>
    <style jsx>{`
      h1 {
        font-family: "Arial", sans-serif;
        display: inline-block;
        width: 100%;
        text-align: center;
      }

      .ProductThumbnail {
        position: relative;
        width: 200px;
        height: 300px;
        float: left;
        overflow: hidden;
      }

      .ProductName {
        font-size: 20px;
        font-family: sans-serif;
        display: inline-block;
        width: 100%;
        text-align: center;
        top: 20px;
        position: relative;
        background-color: rgba(255, 255, 255, 0.75);
      }

      .ProductPrice {
        position: absolute;
        display: inline-block;
        top: 255px;
        width: 100%;
        text-align: center;
        font-size: 25px;
        right: 0px;
        background-color: rgba(255, 255, 255, 0.75);
      }

      .ProductImage {
        height: 100%;
        position: absolute;
        top: 0px;
        right: -30px;
        z-index: -10;
      }

      ul {
        padding: 0;
      }

      li {
        list-style: none;
        margin: 5px 0;
      }

      a {
        text-decoration: none;
        color: blue;
      }

      a:hover {
        opacity: 0.6;
      }
    `}</style>
  </div>
);

Index.getInitialProps = async function() {
  const res = await fetch("http://localhost:8080/products?_page=1&_limit=20");
  const data = await res.json();

  console.log(`Product data fetched. Count: ${data.length}`);

  return {
    products: data
  };
};

export default Index;
